package ex110;
import java.util.Scanner;
public class ex37 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;
        int soma = 0;

        while (i <= 100) {
            soma += i;
            i++;
        }

        System.out.println("A soma dos números de 1 a 100 é: " + soma);
	}

}
